import React, {Component} from "react"

import UserData from "../components/UserData.js"

import "../styles/App.css"

const apiLink = "https://osu.ppy.sh/api/"
const userBest = 'get_user_best?'
const userData = "get_user?"
const mapData = "get_beatmaps?"
const apiKey = 'k=a79d9c01a3b012fc0c0bab91aaa7d09e8b76492b'

const axios = require("axios")

export default class Player extends Component {
    constructor(props) {
        super(props)
        this.state = {
            playerName: "Catulus",
            gamemode: 1,
            playerData: null,
            playerBest: null
        }
        this.fetchAPI = this.fetchAPI.bind(this)
        this.fetchUser = this.fetchUser.bind(this)
        this.fetchBest = this.fetchBest.bind(this)
    }

    fetchAPI(event) {
        event.preventDefault()
        const userInput = event.target.getElementsByTagName("input")
        const modeInput = event.target.getElementsByTagName("select")
        this.setState({
            playerName: userInput.value,
            gamemode: modeInput.value
        })
        this.fetchUser().then(result => {
            this.setState({
                playerData: <UserData info={this.state.playerData}/>,
                // add code to handle player best scores
            })
        })
    }

    fetchUser() {
        const userUrl = apiLink + userData + apiKey + "&u=" + this.state.playerName + "&m=" + this.state.gamemode + "&type=string"
        return axios.get(userUrl)
    }

    fetchBest() {
        const bestUrl = apiLink + userBest + apiKey + "&u=" + this.state.playerName + "&limit=10&m=" + this.state.gamemode + "&type=string"
        return axios.get(bestUrl)
    }

    render() {
        return (
            <div>
                <div className="Subtitle">
                    <form onSubmit={this.fetchAPI} style={{position: "relative", width: "100%"}}>
                        <span style={{margin: "auto auto auto 0"}}>Search by player</span>
                        <span className="fas fa-search"></span>
                        <input name="username" type="text" placeholder="username" defaultValue={this.state.playerName} />
                        <select name="mode" defaultValue= "1">
                            <option value="0">osu!</option>
                            <option value="1">osu!taiko</option>
                            <option value="2">osu!catch</option>
                            <option value="3">osu!mania</option>
                        </select>
                        <input type="submit" value="Search" id="SubmitButton" />
                    </form>
                </div>
                {this.state.playerData}
            </div>
        )
    }

