import React, {Component} from "react"

import "../styles/App.css"

const apiLink = "https://osu.ppy.sh/api/"
const userBest = 'get_user_best?'
const userData = "get_user?"
const mapData = "get_beatmaps?"
const apiKey = 'k=a79d9c01a3b012fc0c0bab91aaa7d09e8b76492b'
const imageLink = "https://a.ppy.sh/"
const countryFlag = "https://osu.ppy.sh/images/flags/"

const axios = require("axios")

export default class Player extends Component {
    constructor(props) {
        super(props)
        this.state = {
            playerID: "6276709",
            gamemode: 1
        }
        this.playerSubmit = this.playerSubmit.bind(this)
        this.playerChange = this.playerChange.bind(this)
        this.modeChange = this.modeChange.bind(this)
    }

    playerSubmit() {
        const bestUrl = apiLink + userBest + apiKey + "&u=" + this.state.playerID + "&limit=10&m=" + this.state.gamemode + "&type=string"
        const userUrl = apiLink + userData + apiKey + "&u=" + this.state.playerID + "&m=" + this.state.gamemode + "&type=string"
    }

    playerChange(event) {
        this.setState({playerID: event.target.value})
    }

    modeChange(event) {
        this.setState({gamemode: event.target.value})
    }

    render() {
        return (
            <div className="Subtitle">
                <form onSubmit={this.playerSubmit} style={{position: "relative", width: "100%"}}>
                    <span style={{margin: "auto auto auto 0"}}>Search by player</span>
                    <input type="text" value={this.state.playerID} onChange={this.playerChange} />
                    <select defaultValue= "1" onChange={this.modeChange}>
                        <option value="0">osu!</option>
                        <option value="1">osu!taiko</option>
                        <option value="2">osu!catch</option>
                        <option value="3">osu!mania</option>
                    </select>
                </form>
            </div>
        )
    }
}
